Write it in Stan

fixed_groups &lt;- cmdstan_model(stan_file = "topics/03_one_random_effect/fixed_groups.stan")

fixed_groups

data {
  int&lt;lower=0&gt; N;
  vector[N] y;
  int&lt;lower=0&gt; Ngroup;
  array[N] int&lt;lower=0, upper=Ngroup&gt; group_id;
}
parameters {
  real b_avg;
  vector[Ngroup] b_group;
  real&lt;lower=0&gt; sigma;
}
model {
  y ~ normal(b_avg + b_group[group_id], sigma);
  b_group ~ std_normal();
  b_avg ~ normal(5, 2);
  sigma ~ exponential(.5);
}
generated quantities {
  vector[N] fake_obs;

  for (i in 1:N) {
    fake_obs[i] = normal_rng(b_avg + b_group[group_id[i]], sigma);
  }

  // predict making one new observation per group
  vector[Ngroup] one_obs_per_group;

  for (k in 1:Ngroup) {
    one_obs_per_group[k] = normal_rng(b_avg + b_group[k], sigma);
  }
}




Fit the model

peng_group_list &lt;- with(penguin_groupid, 
         list(
           N = length(mass_kg),
           y = mass_kg,
           Ngroup = max(group_id),
           group_id = group_id
         ))

fixed_groups_samples &lt;- fixed_groups$sample(
  data = peng_group_list,
  refresh = 0,
  parallel_chains = 4
)

